changeSet.sql:
create table if not exists client ( id bigint primary key, surname varchar(100), name varchar(100), patronymic varchar(100), email varchar(50), phone_number varchar(30), password varchar(100), created_at timestamp default (current_timestamp AT TIME ZONE 'Asia/Almaty'), actual boolean default true );
insert into client values (1, 'Алпысбаев', 'Диас', 'Ерикович', 'alpysbayevdias01@gmail.com', '7701574513');
create table if not exists sms_verification ( id bigint primary key, phone_number varchar(30), code varchar(10), created_at timestamp default (current_timestamp AT TIME ZONE 'Asia/Almaty'), actual boolean default true );
create table if not exists client_token_storage ( id varchar(100) primary key, client_id bigint REFERENCES client (id), created_at timestamp default (current_timestamp AT TIME ZONE 'Asia/Almaty'), actual boolean default true );
create table if not exists client_key_storage ( id varchar(100) primary key, client_id bigint REFERENCES client (id), created_at timestamp default (current_timestamp AT TIME ZONE 'Asia/Almaty'), actual boolean default true );

create table if not exists query_blocked
(
  valuestr varchar(100) primary key
  );
